asyncxml = require('asyncxml')
streamify = require('dt-stream')
xml = new asyncxml.Builder({pretty:true});
streamify(xml).stream.on('data', function (chunk) {
    console.log(chunk);
});
// build some xml
xml.tag("xml", {version:"1.0"})
    .tag("list")
       .tag("entry", function () {
            this.attr('id', 2)
        }).up()
        .tag("entry", {id:2}, "foo").up()
    .up()
.end()
